import { GETALL, GET_DETAILS, FILTER, FILTERCONT } from "../actions/types";

let initialState = {
    countries:[],
    details:[],
    activity:[],
}

export const reducer = (state = initialState, action)=>{
    switch(action.type){
        case GETALL:
            return{
                ...state,
                countries: [...action.payload]
            } 

        case GET_DETAILS:
            return{
                ...state,
                details:[...action.payload]
            }

        case FILTER:
            return{
                ...state,
                countries:[...action.payload]
            }
        case FILTERCONT:
            return{
                ...state,
                countries:[...action.payload]
            }


        default: 
            return state

    }

}
