import { GETALL, GET_DETAILS, FILTER, FILTERCONT, FILTERACTI, GETACTIVITIES, ORDER, PAGE} from "../actions/types";

let initialState = {
    countries:[],
    details:[],
    activity:[],
    getActivities:[],
    order:'',
    pagestart:0,
    pageend:9
}

export const reducer = (state = initialState, action)=>{
    switch(action.type){
        case GETALL:
            return{
                ...state,
                countries: [...action.payload]
            } 

        case GET_DETAILS:
            return{
                ...state,
                details:[...action.payload]
            }

        case FILTER:
            return{
                ...state,
                countries:[...action.payload]
            }
        case FILTERCONT:
            return{
                ...state,
                countries:[...action.payload]
            }
        case FILTERACTI:
            return{
                ...state,
                countries:[...action.payload]
            }
        case GETACTIVITIES:
            return{
                ...state,
                getActivities:[...action.payload]
            }
        case ORDER:
            return{
                ...state,
                order:action.payload
            } 
        case PAGE:
            return{
                ...state,
                pagestart:action.payload+9,
                pagend:action.payload+10

            }


        default: 
            return state

    }

}
