import { GET_COUNTRIES, GET_DETAILS, CREATE_ACTIVITY } from "./types";
import axios from 'axios';


export const getCountries = ()=>{
    return (dispatch)=>{
        axios.get('http://localhost:3001/countries')
        .then(result=>result.data)
        .then(data=>dispatch({
            type:GET_COUNTRIES,
            payload:data
        }))

    }

}
