import { GET_COUNTRIES, GET_DETAILS, CREATE_ACTIVITY } from "../actions/types";

const initialState = {
    countries:[],
    details:[],
    activity:[]
}

export const reducer = (state = initialState, action)=>{
    switch(action.type){
        case GET_COUNTRIES:
            return{
                ...state,
                countries: action.payload
            } 


        default: 
            return state

    }

}
