import React,{ useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import  {Countries}  from '../elements/Countries'
import { filter, filterContinet, Order, PageE, PageS} from '../../Countries_store/actions'
import { useDispatch, useSelector} from 'react-redux'
import { Activities } from '../elements/Activities'





export const Home = ()=>{
    const dispatch = useDispatch()
    let $PE = useSelector((state)=>state.PE)
    let $PS = useSelector((state)=>state.PS)
    let $countries = useSelector((state)=>state.countries)

    const [search, setSearch] = useState({
                                          filname:'', 
                                          filcontinent:'',
                                          pageStart:0,
                                          pageEnd:9
                                        })

    const handlechange = (e)=>{
        e.preventDefault()
        setSearch((prev)=>({...prev,
                   [e.target.name]:e.target.value,

                  }))
    }
    const handleSubmit = (e)=>{
        e.preventDefault()

            if(e.target.value){
                dispatch(filter(search.filname))
            }
    }

    const handleSubmitContinent = (e)=>{
        e.preventDefault()
        if(e.target.value){
            dispatch(filterContinet(search.filcontinent))
        }

    }

    const handleOrder = (e)=>{
        e.preventDefault()
        dispatch(Order(e.target.value))   
    }

    const handleReset = (e)=>{
        e.preventDefault()
        if(e.target.value){
            dispatch(filter(''))
        }


    }

    const handlePage = (e)=>{
        e.preventDefault()
        if(e.target.value === 'p+'){
            setSearch((prev)=>({
               ...prev,
               pageStart:search.pageStart + 9,
               pageEnd:search.pageEnd + 10

            }))
        }
        if(e.target.value === 'p-'){
            setSearch((prev)=>({
                ...prev,
                pageStart:search.pageStart - 9,
                pageEnd:search.pageEnd - 10
            }))
        }
    }
    let PS = search.pageStart
    let PE = search.pageEnd

    useEffect(()=>{
        dispatch(PageS(PS))
        dispatch(PageE(PE))
    },[search.pageEnd, search.pageStart])

    let Hidden = (!search.filcontinent)?true:false
    let HiddenPageB = ($PS < 0)?true:false
    let HiddenPageN = ($PE >= 279)?true:false


    return(
        <>
        <Link to='/create_activities'><button>Create Activities</button></Link>
         <h1>HOME</h1>
         <form >
         <input type='text' placeholder='country...' name='filname' value={search.filname} onChange={handlechange}></input>
         <input type='submit'value='search' onClick={handleSubmit}/>
         <button onClick={handleReset} name='reset' value='reset'>Reset</button>
         </form>
         <div>
         </div>

         <select onChange={handlechange} onSubmit={handleSubmitContinent} name='filcontinent' value={search.filcontinent}>
             <option hidden={true}>--SELECT CONTINENT--</option>
             <option>ASIA</option>
             <option>ANTARCTICA</option>
             <option>AFRICA</option>
             <option>EUROPE</option>
             <option>NORTH AMERICA</option>
             <option>OCEANIA</option>
             <option>SOUTH AMERICA</option>
         </select>
         <input hidden={Hidden} type='submit'value='searchContinent' onClick={handleSubmitContinent}/>

         <br/>
         <br/>
         <br/>
         <Activities/>
         <br/>
         <br/>
         <br/>

         <button type='submit'value='AZ' name='order' onClick={handleOrder}>A-Z</button>
         <button type='submit'value='ZA' name='order' onClick={handleOrder}>Z-A</button>
         <button type='submit'value='POP+' name='order' onClick={handleOrder}>Population +</button>
         <button type='submit'value='POP-' name='order' onClick={handleOrder}>Population -</button>

         <br/>
         <br/>
         <button disabled={HiddenPageB} value='p-' onClick={handlePage}>{`<==`}</button>
         <div>{$PE} DE {$countries.length/10}</div>
         <button disabled={HiddenPageN} value='p+' onClick={handlePage}>{`==>`}</button><br/>
             <Countries/>
        </>
    )
}
