import React,{ useEffect, useState} from 'react'
import { Link } from 'react-router-dom'
import { useDispatch,useSelector } from 'react-redux'
import { getCountries, ActivityPost } from '../../Countries_store/actions'
// import axios from 'axios'

export const CreateActivity = ()=>{
    const dispatch = useDispatch() 
    useEffect(()=>{
        dispatch(getCountries())

    },[dispatch])

    let $countries = useSelector((state)=>state.countries)
    let [act, setAct] = useState({
        name:'',
        difficulty:1,
        duration:30,
        season:'SPRING',
        countryID:[]
    })
    let handleChange = (e)=>{
        e.preventDefault()
            setAct((prev)=>({
                ...prev,
                // countries:[...e.target.value,act.countries],
                [e.target.name]: e.target.value
              }))
    }

    let handleCountries = (e)=>{
        if(act.countryID.includes(e.target.value)){
            console.log('ya esta en el estado')
        }else{

            setAct((prev)=>({
                ...prev,
                countryID:[...act.countryID,e.target.value]
            }))
        }
    }
    let handleClickDelete = (e)=>{
        e.preventDefault()
        console.log(e.target.name)
        let arr = act.countryID.filter((el)=>el !== e.target.value)
        setAct((prev)=>({
            ...prev,
            countryID:[...arr]
        }))
    }
    let handleSubmit = async(e)=>{
       e.preventDefault()
       if(!act.name){alert('name no found')}
       if(act.countryID.length === 0){
           alert('country no add yet')
        }else{

            ActivityPost(act)
            setAct(()=>({
                name:'',
                difficulty:1,
                duration:30,
                season:'SPRING',
                countryID:[]
            }))
            let mensaje;
            let opcion = window.confirm("Clicka en Aceptar o Cancelar");
            if (opcion) {
        mensaje = "Has clickado OK";
	} else {
	    mensaje = "Has clickado Cancelar";
	}

        }

    }


    return(
        <>
           <Link to='/home'><button>HOME</button></Link>
            <h5>Activity create</h5>
            <form>
            <label>NAME ACTIVITY</label><br/>
            <input placeholder='name' name='name' value={act.name} onChange={handleChange}/>
            <br/>
            <br/>
            <label>DIFFICULTY ACTIVITY</label>
            <br/>
            <select name='difficulty' value={act.difficulty} onChange={handleChange}>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
            </select>
            <br/>
            <br/>
            <label>DURATION ACTIVITY</label>
            <br/>
            <select name='duration' value={act.duration} onChange={handleChange}>
                <option>30</option>
                <option>60</option>
                <option>90</option>
                <option>120</option>
            </select><label>Minutes</label>
            <br/>
            <br/>
            <label>SEASON</label>
            <select name='season' value={act.season} onChange={handleChange}>
                <option>SPRING</option>
                <option>SUMMER</option>
                <option>AUTUMN</option>
                <option>WINTER</option>
            </select>
            <br/>
            <br/>
            <label>PRACTICE COUNTRIES</label>
            <div>
            <select style={{width:'120px',height:'160px'}} multiple={true} name='countryID' value={[]} onChange={handleCountries}>
            {$countries.map((el)=><option style={{backgroundImage:`url(${el.imageflag}`,
                                                  backgroundRepeat: 'no-repeat',
                                                  backgroundSize: 'cover',
                                                  width:'40px',
                                                  height:'15px'}}
                                          key={el.id}>{el.id}</option>)}

            </select>
            <div>
                <h6>countries selectd</h6>
                {act.countryID.map((el)=><button key={el} value={el} onClick={handleClickDelete}>{el}  X</button>)}
            </div>
            </div>
            <br/>
            <br/>
            <br/>

            <button name='create' onClick={handleSubmit}>CREATE</button>
            <button name='decline' onClick={handleChange}>DECLINE</button>

            </form>


        </>
    )
}
