import { GETALL, GET_DETAILS, FILTER, FILTERCONT, FILTERACTI } from "./types";
import axios from 'axios';

export const getDetail = (id)=>{
    return async(dispatch)=>{
        let get = await axios.get(`http://localhost:3001/countries/${id}`)
        dispatch({
            type: GET_DETAILS,
            payload: get.data
        })
    }
}

export const getCountries = ()=>{
    return async(dispatch)=>{
        let get = await axios.get('http://localhost:3001/countries')
        dispatch({
            type: GETALL,
            payload: get.data
        })
    }
}

export const filter = (value)=>{
    console.log(value)
    return async(dispatch)=>{
        let get = await axios.get(`http://localhost:3001/countries?name=${value}`)
        dispatch({
            type: FILTER,
            payload: get.data
        })

    }
}

export const filterContinet = (value)=>{
        value = value.toLowerCase()
            return async(dispatch)=>{
                let get = await axios.get('http://localhost:3001/countries')
                let fill = get.data.filter((el)=>el.continent.toLowerCase() === value)
                dispatch({
                    type: FILTERCONT,
                    payload: fill
                })
            }

}

export const filterActivity = (value)=>{
     value = value.toLowerCase()
           return async(dispatch)=>{
                let get = await axios.get('http://localhost:3001/activity')
                let data = get.data
                let datafill= data.filter((el)=> el.name === value)
                let fill = datafill.map((el)=>el.countries.map((el)=>el))
                // let fill1 = fill.map((el)=>el)
                dispatch({
                    type: FILTERACTI,
                    payload: fill
                })
           }

}
