import { GETALL } from "./types";
import axios from 'axios';


export const getCountries = ()=>{
    return async(dispatch)=>{
        let get = await axios.get('http://localhost:3001/countries')
        dispatch({
            type: GETALL,
            payload: get.data
        })
    }
}


