import React,{ useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useSelector } from 'react-redux'

export const CreateActivity = ()=>{

    let $countries = useSelector((state)=>state.countries)
    let [check, setCheck] = useState(false)
    let [act, setAct] = useState({
        name:'',
        difficulty:1,
        duration:30,
        season:'',
        countries:[]
    })
    let handleChange = (e)=>{
        e.preventDefault()
            setAct((prev)=>({
                ...prev,
                countries:[...e.target.value,act.countries],
                [e.target.name]: e.target.value
              }))
    }
    let arr = [];
    let handleCountries = (e)=>{
        let arrP= [];
        // e.preventDefault()
       if(!e.target.checked){
           setCheck(true)
           e.target.checked = check
        //    let arrF = arr.filter((el)=>el !== e.target.value)
        //    arr = [...arr,...arrF]
       }

        // setCheck(false)
        // if(arr.includes(e.target.value)){
        //     return
        // }else{

        //     arr.push(e.target.value)
        // }
        // console.log('esto es el estado', check)
        console.log(e.target.checked)
    }
    console.log(arr)


    return(
        <>
           <Link to='/home'><button>HOME</button></Link>
            <h5>Activity create</h5>
            <form>
            <label>NAME ACTIVITY</label><br/>
            <input placeholder='name' name='name' value={act.name} onChange={handleChange}/>
            <br/>
            <br/>
            <label>DIFFICULTY ACTIVITY</label>
            <br/>
            <select name='difficulty' value={act.difficulty} onChange={handleChange}>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
            </select>
            <br/>
            <br/>
            <label>DURATION ACTIVITY</label>
            <br/>
            <select name='duration' value={act.duration} onChange={handleChange}>
                <option>30</option>
                <option>60</option>
                <option>90</option>
                <option>120</option>
            </select><label>Minutes</label>
            <br/>
            <br/>
            <label>SEASSON</label>
            <select name='seasson' value={act.season} onChange={handleChange}>
                <option>SPRING</option>
                <option>SUMMER</option>
                <option>AUTUMN</option>
                <option>WINTER</option>
            </select>
            <br/>
            <br/>
            <label>PRACTICE COUNTRIES</label>
            <div>
            <input type='checkbox' value={1} onChange={handleCountries} checked={false}/>input1
            <input type='checkbox' value={2} onChange={handleCountries} checked={false}/>input2
            <input type='checkbox' value={3} onChange={handleCountries} checked={false}/>input3
            <input type='checkbox' value={4} onChange={handleCountries} checked={false}/>input4
           {/* {$countries.map((el)=><div key={el.id}><input name='countries' value={el.id} onChange={handleChange} type='checkbox'/>{el.id}<div style={{backgroundImage:`url(${el.imageflag})`,
                                              height:'20px',
                                              width:'40px',
                                              backgroundRepeat: 'no-repeat',
                                              backgroundPosition: 'center',
                                              backgroundSize: 'cover'
                                            }}></div></div>)} */}
            </div>
            <br/>
            <br/>
            <br/>

            <button name='create' onClick={handleChange}>CREATE</button>
            <button name='decline' onClick={handleChange}>DECLINE</button>

            </form>


        </>
    )
}
