import React from 'react'
import { filterActivity } from '../../Countries_store/actions'
import { useDispatch, useSelector } from 'react-redux'

export const Activities = ()=>{
    const $activities = useSelector((state)=>state.getActivities)
    const dispatch = useDispatch()

    let handleClick = (e)=>{
        let activity = e.target.name
            dispatch(filterActivity(activity))

    }



    return(
        <>
           {$activities.map((el)=><button key={el.id} name={el.name} onClick={handleClick}>{el.name}</button>)}
        </>
    )
}
