import { GET_COUNTRIES, GET_DETAILS, CREATE_ACTIVITY } from "./types";
import axios from 'axios';


export let getCountries = ()=>{
    return (dispatch)=>{
        axios.get('http://localhost:3001/countries')
        .then(res=>res.data)
        .then(data=>dispatch({
            type:GET_COUNTRIES,
            payload:data
        }))

    }

}
