import { GETALL } from "../actions/types";

let initialState = {
    countries:[]
    // details:[],
    // activity:[],
}

export const reducer = (state = initialState, action)=>{
    switch(action.type){
        case GETALL:
            console.log('hola tu',action.payload)
            return{
                ...state,
                countries: [...action.payload]
            } 


        default: 
            return state

    }

}
