import { GET_COUNTRIES, GET_DETAILS, CREATE_ACTIVITY } from "./types";
import axios from 'axios';




export let getCountries = async()=>{
    let get = await axios.get('http://localhost:3001/countries')
    let res = await get.data
    return (dispatch)=>{
       dispatch({
           type: GET_COUNTRIES,
           payload: res
       })
    }

}
