import React,{ useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import  {Countries}  from '../elements/Countries'
import { filter } from '../../Countries_store/actions'
import { useDispatch } from 'react-redux'





export const Home = ()=>{
    const [search, setSearch] = useState({filname:'', filcontinent:''})
    const dispatch = useDispatch()

    const handlechange = (e)=>{
        e.preventDefault()
        setSearch((prev)=>({...prev,
                   [e.target.name]:e.target.value,

                  }))
    }
    const handleSubmit = (e)=>{
        e.preventDefault()
        if(e.target.value){
            dispatch(filter(search.filname))
        }
    }
    const handleReset = (e)=>{
        e.preventDefault()
        if(e.target.value){
            dispatch(filter(''))
        }

    }

    //pendiente filtrado contiente




//   console.log(search)



    return(
        <>
        <Link to='/create_activities'><button>Create Activities</button></Link>
         <h1>HOME</h1>
         <form >
         <input type='text' placeholder='country...' name={search.filname} onChange={handlechange}></input>
         <input type='submit'value='search' onClick={handleSubmit}/>
         <button onClick={handleReset} name='reset' value='reset'>Reset</button>
         </form>

         <div>
         </div>
         <input type='text' placeholder='continent...' name={search.filcontinent} onchange={handlechange}></input>
         <input type='submit'value='Continent Fill'/>
         <br/>
         <br/>
         <input type='text' placeholder='activity...' name='activity'></input>
         <input type='submit'value='Activity Fill'/>
         <br/>
         <br/>
         <input type='submit'value='Order A-Z'/>
         <input type='submit'value='Order Z-A'/>
         <input type='submit'value='Order Population'/>
         <br/>
         <br/>
         <button>{`<==`}</button>
         <button>{`==>`}</button><br/>
             <Countries/>





        </>
    )
}
