import { useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { filter } from "../../Countries_store/actions"




export const Search = (value)=>{
    value = value.toLowerCase()
    const dispatch = useDispatch()
    let $countries = useSelector((state)=>state.countries)

    useEffect(()=>{
        dispatch(filter($countries.filter((el)=>el.name.toLowerCase().includes(value))))
    },[dispatch])

    // let pri = $countries.filter((el)=>el.name.toLowerCase().includes(value))
//    console.log(pri)
}
