    //               case $order === 'POP':
    //                   return (a,b)=>{
    //                 if(a.population < b.population)return 1
    //                 if(a.population > b.population)return -1
    //                 return 0
    //               }

    //               default:
    //             return $order  
    //         }
    // }
    let order;
        if($order === 'AZ'){
            order = (a,b)=>{
            if(a.name < b.name)return 1
            if(a.name > b.name)return -1
            return 0
           }
         }
        if($order === 'ZA'){
            order = (a,b)=>{
            if(a.name < b.name)return -1
            if(a.name > b.name)return 1
            return 0
           }
        }
        if($order === 'POP'){
            order = (a,b)=>{
                if(a.population < b.population)return 1
                if(a.population > b.population)return -1
                return 0
            }

        }







    useEffect(()=>{
        dispatch(getCountries())
        dispatch(getAllActivities())
   },[dispatch])

    return(
        <>
           {$countries.length?$countries.sort(order).map((el)=><Country key={el.id}
                                          id={el.id}
                                          image={el.imageflag}
                                          name={el.name}
                                          continent={el.continent} 
           />):'no found'}
        </>
    )
}
