import React,{ useState } from 'react'
import { Link } from 'react-router-dom'
import  {Countries}  from '../elements/Countries'
import { filter, filterContinet } from '../../Countries_store/actions'
import { useDispatch } from 'react-redux'





export const Home = ()=>{
    const [search, setSearch] = useState({filname:'', filcontinent:'',filactivity:''})
    const dispatch = useDispatch()

    const handlechange = (e)=>{
        e.preventDefault()
        setSearch((prev)=>({...prev,
                   [e.target.name]:e.target.value,

                  }))
    }
    const handleSubmit = (e)=>{
        e.preventDefault()

            if(e.target.value){
                dispatch(filter(search.filname))
            }
    }

    const handleSubmitContinent = (e)=>{
        e.preventDefault()
        if(e.target.value){
            dispatch(filterContinet(search.filcontinent))
        }

    }

    const handleSubmitActivity = (e)=>{
        e.preventDefault()
      console.log(search.filactivity)

    }
    const handleReset = (e)=>{
        e.preventDefault()
        if(e.target.value){
            dispatch(filter(''))
        }


    }
    let Hidden = (!search.filcontinent)?true:false


    return(
        <>
        <Link to='/create_activities'><button>Create Activities</button></Link>
         <h1>HOME</h1>
         <form >
         <input type='text' placeholder='country...' name='filname' value={search.filname} onChange={handlechange}></input>
         <input type='submit'value='search' onClick={handleSubmit}/>
         <button onClick={handleReset} name='reset' value='reset'>Reset</button>
         </form>
         <div>
         </div>

         <select onChange={handlechange} onSubmit={handleSubmitContinent} name='filcontinent' value={search.filcontinent}>
             <option hidden={true}>--SELECT CONTINENT--</option>
             <option>ASIA</option>
             <option>ANTARCTICA</option>
             <option>AFRICA</option>
             <option>EUROPE</option>
             <option>NORTH AMERICA</option>
             <option>OCEANIA</option>
             <option>SOUTH AMERICA</option>
         </select>
         <input hidden={Hidden} type='submit'value='searchContinent' onClick={handleSubmitContinent}/>

         <br/>
         <br/>
         <input type='text' placeholder='activity...' name='filactivity' value={search.filactivity} onChange={handlechange}></input>
         <input type='submit'value='Activity Fill' onClick={handleSubmitActivity}/>
         <br/>
         <br/>
         <input type='submit'value='Order A-Z'/>
         <input type='submit'value='Order Z-A'/>
         <input type='submit'value='Order Population'/>
         <br/>
         <br/>
         <button>{`<==`}</button>
         <button>{`==>`}</button><br/>
             <Countries/>





        </>
    )
}
